import React from 'react';
import './App.css';

const axios = require('axios');

class Tweet extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            tweetID: -1,
            tweetTimeStamp: "",
            tweetAuthor: "",
            tweetGeoLoc: "",
            tweetNbRetweet: -1,
            tweetNbLikes: -1,
            tweetMessage: ""
        }
    }

    render() {
        return (
            <tr>
                <td> {this.state.tweetID}</td>
                <td> {this.state.tweetTimeStamp}</td>
                <td> {this.state.tweetAuthor}</td>
                <td> {this.state.tweetNbRetweet}</td>
                <td> {this.state.tweetNbLikes}</td>
                <td> {this.state.tweetGeoLoc}</td>
                <td> {this.state.tweetMessage}</td>
            </tr>
        );
    }
}

class TweetList extends React.Component {
    render() {
        return (
            <table className="table">
                <thead>
                <tr>
                    <th>#</th>
                    <th>Time</th>
                    <th>Author</th>
                    <th>#Retweet</th>
                    <th>#Likes</th>
                    <th>GeoLoc</th>
                    <th>Message</th>
                </tr>
                </thead>
                <tbody>
                <Tweet></Tweet>
                </tbody>
            </table>
        );
    }
}

class Search extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            searchQuery: "Enter keyword ...",
            tweetList: []
        };

        this.handleSearchInputChange = this.handleSearchInputChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleSearchInputChange(e) {
        this.setState({searchQuery: e.target.value});
    }

    handleSubmit(e) {
        e.preventDefault();
        if (this.state.searchQuery.length === 0) {
            return;
        }

        console.log("data: " + this.state.searchQuery)

        axios.post('http://127.0.0.1:5000/search', {
            query: this.state.searchQuery
        })
            .then(function (response) {
                console.log(response);
            })
            .catch(function (error) {
                console.log(error);
            });
    }

    render() {
        return (
            <form onSubmit={this.handleSubmit} className="input-group mb-3">
                <input type="text" className="form-control" />
                <input type="submit" value="Search" />
            </form>
        );
    }
}


class App extends React.Component {
    constructor(props) {
        super(props);
    }

    render() {
        return (
            <div>
                <Search></Search>
                <TweetList></TweetList>
            </div>
        );
    }
}

export default App;
